import { useParams, useLocation, NavLink } from 'react-router-dom';
import { Button, Image, Tag } from 'antd';
import { WalletOutlined } from '@ant-design/icons';
const fallbackImg =
  'data:image/png;base64,';

const ShowBox = ({ image }) => (
  <div className="flex flex-row justify-start items-start white-glassmorphism p-3 m-2 cursor-pointer hover:shadow-xl">
    <Image
      preview={false}
      src={image || 'error'}
      fallback={fallbackImg}
      alt=""
      style={{ height: '100%', width: '100%' }}
    />
  </div>
);

const NFTToken = () => {
  const { id: collectionId, tokenId } = useParams();
  const location = useLocation();
  const { nft, collection } = location.state;
  const styles = {
    collectionName: {
      color: '#3c14dd',
      fontSize: '18px',
    },
    nftName: {
      flex: '0 0 100%',
      maxWidth: '100%',
      marginBottom: 16,
      fontWeight: 700,
      fontSize: 30,
      wordWrap: 'break-word',
    },
    buyBtn: {
      margin: 16,
    },
  };
  return (
    <div className="flex w-full justify-center items-center gradient-bg-services">
      <div className="flex mf:flex-row flex-col items-start justify-between md:p-20 py-12 px-4">
        <div className="flex-1 flex flex-col justify-start items-start">
          <ShowBox image={nft.image}></ShowBox>
          <div>
            {nft.symbol}
            {nft.metadata.name}
            {nft.metadata.description}
          </div>
          <div>{'collection info'}</div>
          <div>{'nft blockchain info'}</div>
        </div>

        <div className="flex-1 flex flex-col justify-start items-start">
          <div style={styles.collectionName}>
            <NavLink to={`/collection/${collectionId}`}>
              {collection.name}
            </NavLink>
          </div>
          <div style={styles.nftName}>{nft.name}</div>
          <div>#{nft.token_id}</div>
          <div>Owner:{'nft.owner'}</div>
          {nft.metadata && nft.metadata.tags && (
            <>
              <div>
                {nft.metadata.tags.split(',').map((tag) => (
                  <Tag color="cyan">{tag}</Tag>
                ))}
              </div>
            </>
          )}
          <div>{'nft.price'} ETH #.## $ ###.##</div>
          <div style={styles.buyBtn}>
            <Button
              type="primary"
              shape="round"
              icon={<WalletOutlined style={{ verticalAlign: 'baseline' }} />}
              size="large"
            >
              Buy Now
            </Button>
          </div>
          <div>{'nft.transactions table'}</div>
        </div>
      </div>
    </div>
  );
};

export default NFTToken;
